insert into book (id, link, cover, title, subtitle, authors, description, price) VALUES (DEFAULT, 'http://books.google.ru/books?id=NPbkDwAAQBAJ&printsec=frontcover&hl=&source=gbs_api', 'http://books.google.com/books/content?id=NPbkDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'JavaScript: The Definitive Guide', 'Master the World Most-Used Programming Language', ARRAY['David Flanagan'], 'For web developers and other programmers interested in using JavaScript, this bestselling book provides the most comprehensive JavaScript material on the market.', 29);
insert into book (id, link, cover, title, subtitle, authors, description, price) VALUES (DEFAULT, 'http://books.google.ru/books?id=_i6bDeoCQzsC&printsec=frontcover&hl=&source=gbs_api', 'http://books.google.com/books/content?id=_i6bDeoCQzsC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Clean Code', 'A Handbook of Agile Software Craftsmanship', ARRAY['Robert C. Martin'], 'Even bad code can function. But if code isn’t clean, it can bring a development organization to its knees. Every year, countless hours and significant resources are lost because of poorly written code. But it doesn’t have to be that way.', 30);
insert into book (id, link, cover, title, subtitle, authors, description, price) VALUES (DEFAULT, 'http://books.google.ru/books?id=eSIsAwAAQBAJ&printsec=frontcover&hl=&source=gbs_api', 'http://books.google.com/books/content?id=eSIsAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Head First JavaScript Programming', 'A Brain-Friendly Guide', ARRAY['Eric Freeman','Elisabeth Robson'], 'What will you learn from this book? This brain-friendly guide teaches you everything from JavaScript language fundamentals to advanced topics, including objects, functions, and the browser’s document object model.', 22);
insert into book (id, link, cover, title, subtitle, authors, description, price) VALUES (DEFAULT, 'http://books.google.ru/books?id=Fv1wRM_nIPIC&printsec=frontcover&hl=&source=gbs_api', 'http://books.google.com/books/content?id=Fv1wRM_nIPIC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'AIR for Javascript Developers Pocket Guide', 'Getting Started with Adobe AIR', ARRAY['Mike Chambers','Daniel Dura', 'Kevin Hoyt', 'Dragos Georgita'], 'This book is the official guide to Adobe ® AIR[TM], written by members of the AIR team. With Adobe AIR, web developers can use technologies like HTML and JavaScript to build and deploy web applications to the desktop.', 9);
insert into book (id, link, cover, title, subtitle, authors, description, price) VALUES (DEFAULT, 'http://books.google.ru/books?id=FSVTDwAAQBAJ&printsec=frontcover&hl=&source=gbs_api', 'http://books.google.com/books/content?id=FSVTDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Eloquent JavaScript, 3rd Edition', 'A Modern Introduction to Programming', ARRAY['Marijn Haverbeke'], 'Completely revised and updated, this best-selling introduction to programming in JavaScript focuses on writing real applications. JavaScript lies at the heart of almost every modern web application.', 37);
insert into book (id, link, cover, title, subtitle, authors, description, price) VALUES (DEFAULT, 'http://books.google.ru/books?id=pvmjldfuwnQC&printsec=frontcover&hl=&source=gbs_api', 'http://books.google.com/books/content?id=pvmjldfuwnQC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'JavaScript & DHTML Cookbook', 'Solutions & Examples for Web Programmers', ARRAY['Danny Goodman'], 'In today Web 2.0 world, JavaScript and Dynamic HTML are at the center of the hot new approach to designing highly interactive pages on the client side. With this environment in mind, the new edition of this book offers bite-sized solutions.', 16);